import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {

  myBreak:number;
  mySession:number;
  myStart:boolean;
  myPause:boolean;
  sessionTimer:any;
  breakTimer:any;
  timer:any;
  currentTime:string;

  ngOnInit() {
    this.myBreak = 5;
    this.mySession = 25;
    this.myStart = true;
    this.myPause = false;
    this.sessionTimer = this.mySession;
    this.breakTimer = this.myBreak;
    this.currentTime = (this.mySession * 60).toString();
  };

  // METHODS
  decreaseBreak() {
    console.log("Decrease Break");
    this.myBreak = this.myBreak === 0 ? 0 : this.myBreak - 1;
  };

  increaseBreak() {
    console.log("Increase Break");
    this.myBreak = this.myBreak === this.mySession ? this.mySession : this.myBreak + 1;
  };

  decreaseSession() {
    console.log("Decrease Session");
    this.mySession = this.mySession === 0 ? 0 : this.mySession - 1;
    this.currentTime = (this.mySession * 60).toString();
  };

  increaseSession() {
    console.log("Increase Session");
    this.mySession = this.mySession === 60 ? 25 : this.mySession + 1;
    this.currentTime = (this.mySession * 60).toString();
  };

  startSession() {
    console.log("Started session");
    // number of pomodoro sessions => 1 session = session + break
    let numberOfSessions:number = 1;
    // 'root' object is set *by reference* to the global 'this' object
    let root = this;
    let temp_mySession = root.mySession * 60;
    let temp_myBreak = root.myBreak * 60;
    // creating a regular timer to clock the elapsed time
    for (let i=0; i<=(numberOfSessions * (temp_mySession + temp_myBreak)); i++) {
      this.timer = setTimeout((function(x) {
        return function() {
          if (x>temp_mySession) {
            root.currentTime = ((temp_mySession + temp_myBreak) - x).toString();
          } else {
            root.currentTime = (temp_mySession - x).toString();
          }
          console.log("Timer: " + x.toString() + " s elapsed");
        }
      })(i), i * 1000);
    }
    // creating 4 pomodoro sessions
    for (let i=1; i<=numberOfSessions; i++) {
      // setting the session timeout
      this.sessionTimer = setTimeout((function(x) {
        // immediately invoke the outer function with passed argument
        // and return a callback function that 'remembers' its execution context
        return function() {
          if (x===numberOfSessions) {
            // any change applied to 'root'
            // will also be applied to 'this'
            // ('root' is set by reference)
            //
            // reset buttons
            root.myStart = true;
            root.myPause = false;
            // root.currentTime = (root.mySession - x).toString();
            // sound it off
            root.beep();
            console.log(" -> Session " + x.toString() + " finished.");
          } else {
            // root.currentTime = (root.mySession - x).toString();
            console.log(" -> Session " + x.toString() + " finished.");
          };
        };
        // have to add the previous break interval into the next session
      })(i), (i * temp_mySession + (i - 1) * temp_myBreak) * 1000);
      // setting the break timeout right after each session
      // if (i<=(numberOfSessions-1)) {
      this.breakTimer = setTimeout((function(x) {
        // immediately invoke the outer function with passed argument
        // and return a callback function that 'remembers' its execution context
        return function() {
          // root.currentTime = (root.myBreak - x).toString();
          console.log(" -> Break from session " + x.toString() + " finished.");
        };
      })(i), i * (temp_mySession + temp_myBreak) * 1000);
      // }
    };
  };

  pauseSession() {
    console.log("Pause Session");
  };

  stopSession() {
    console.log("Stop Session");
    // stop all timers
    while (this.sessionTimer--) {
      clearTimeout(this.sessionTimer);
    }
    while (this.breakTimer--) {
      clearTimeout(this.breakTimer);
    }
    // reset buttons
    this.myStart = true;
    this.myPause = false;
    this.mySession = 25;
    this.myBreak = 5;
    this.currentTime = (this.mySession * 60).toString();
  }

  beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
  };

}
